
def judge(dna):

    max_space = 2
    side_bonus = 1
    threshold_score = 2

    maxcount = 0
    count = 0
    exitcount = 0
    in_kmer = False
    len_dna = len(dna)

    for pos,value in enumerate(dna):

        if value == 1:
            in_kmer = True

        if in_kmer:

            if value == 0:
                exitcount += 1
            else:
                count += 1

                if pos in [0, len_dna-1]:
                    count += side_bonus

            if count > maxcount:
                maxcount = count
                

            if exitcount >= max_space:
                count = 0
                exitcount = 0
                in_kmer = False

        
    return maxcount >= threshold_score   


